import React from "react";
import {Helmet} from "react-helmet";
import Config from "../Config.json";

const TITLE = "News | " + Config.SITE_TITLE;
const DESC = "This is the news page of the VIStA Research Group.";
const CANONICAL = Config.SITE_DOMAIN + "/news";

class News extends React.Component{

    render() {
        let slides = document.getElementsByClassName("slider-slide");
        let navlinks = document.getElementsByClassName("slider__navlink");
        let currentSlide = 0;

        document.getElementById("nav-button-next").addEventListener("click", () => {
            changeSlide(currentSlide + 1)
        });
        document.getElementById("nav-button-prev").addEventListener("click", () => {
            changeSlide(currentSlide - 1)
        });

        function changeSlide(moveTo) {
            if (moveTo >= slides.length) {moveTo = 0;}
            if (moveTo < 0) {moveTo = slides.length - 1;}

            slides[currentSlide].classList.toggle("active");
            navlinks[currentSlide].classList.toggle("active");
            slides[moveTo].classList.toggle("active");
            navlinks[moveTo].classList.toggle("active");

            currentSlide = moveTo;
        }

        document.querySelectorAll('.slider-navlink').forEach((bullet, bulletIndex) => {
            bullet.addEventListener('click', () => {
                if (currentSlide !== bulletIndex) {
                    changeSlide(bulletIndex);
                }
            })
        })

        return (<main>

             <Helmet>
                <title>{TITLE}</title>
                <link rel="canonical" href={CANONICAL} />
                <meta name="description" content={DESC}/>
                <meta name="theme-color" content={Config.THEME_COLOR} />
            </Helmet>

            <h3>News</h3>
            <div class="news-container">
                <year>2022</year>
                <month-slider>
                    <div class="allmonthslider">
                        <div class="slider-Slides">
                            <div class="slider-slide active">
                                First
                            </div>
                            <div class="slider-slide">
                                Second
                            </div>
                        </div>

                        <div id="nav-button-prev" class="slider-nav-button"></div>
                        <div id="nav-button-next" class="slider-nav-button"></div>
                        <div class="slider-nav">
                            <div class="slider-navlink active"></div>
                            <div class="slider-navlink"></div>
                        </div>
                    </div>
                </month-slider>


            </div>
        </main>);
    }
}

export default News;
