        //     slides[moveTo].classList.toggle("active");
        //     navlinks[moveTo].classList.toggle("active");

        //     currentSlide = moveTo;
        // }

        // document.querySelectorAll('.slider-navlink').forEach((bullet, bulletIndex) => {
        //     bullet.addEventListener('click', () => {
        //         if (currentSlide !== bulletIndex) {
        //             changeSlide(bulletIndex);
        //         }
        //     })
        // })

        return (<main>

             <Helmet>
                <title>{TITLE}</title>
                <link rel="canonical" href={CANONICAL} />
                <meta name="description" content={DESC}/>
                <meta name="theme-color" content={Config.THEME_COLOR} />
            </Helmet>

            <h3>News</h3>
            <div class="news-container">
                <year>2022</year>
                <month-slider>
                    <div class="CSSgal">
                    <s id="s1"></s> 
                    <s id="s2"></s>
                    <s id="s3"></s>
                    <s id="s4"></s>

                    <div class="slider">
                        <div style="background:#5b8;">
                                <h2>PURE <b>CSS</b> SLIDESHOW</h2>
                                <p>Responsive Slideshow Gallery created using CSS onlyby Roko</p>
                            </div>
                        <div style="background:#85b;">
                                <h2>Slide 2</h2>
                            </div>
                        <div style="background:#e95;">
                                <h2>Slide 3</h2>
                            </div>
                        <div style="background:#e59;">
                                <h2>Slide 4</h2>
                            </div>
                    </div>

                    <div class="prevNext">
                        <div><a href="#s4"></a><a href="#s2"></a></div>
                        <div><a href="#s1"></a><a href="#s3"></a></div>
                        <div><a href="#s2"></a><a href="#s4"></a></div>
                        <div><a href="#s3"></a><a href="#s1"></a></div>
                    </div>

                    <div class="bullets">
                        <a href="#s1">1</a>
                        <a href="#s2">2</a>
                        <a href="#s3">3</a>
                        <a href="#s4">4</a>
                    </div>

                    </div>
                </month-slider>


            </div>
        </main>);
    }
}

export default News;
